INSERT INTO threat_and_sec_schema.main_techniques (id, technique_number, technique_description)
VALUES (114, 6, 'Копирование вредоносного кода на съемные носители');
INSERT INTO threat_and_sec_schema.main_techniques (id, technique_number, technique_description)
VALUES (115, 7, 'Размещение вредоносных программных модулей на разделяемых сетевых ресурсах в сети');
INSERT INTO threat_and_sec_schema.main_techniques (id, technique_number, technique_description)
VALUES (116, 8,
        'Использование доверенных отношений скомпрометированной системы и пользователей этой системы с другими системами и пользователями для распространения вредоносного программного обеспечения или для доступа к системам и информации в других системах и сетях. Пример: отсылка сообщений корпоративной электронной почты от имени коллег и прочих доверенных лиц');
INSERT INTO threat_and_sec_schema.main_techniques (id, technique_number, technique_description)
VALUES (117, 1,
        'Доступ к системе для сбора информации и вывод информации через стандартные протоколы управления (например, RDP, SSH), а также использование инфраструктуры провайдеров средств удаленного администрирования. Пример: использование средств удаленного управления RMS/teamviewer для создания канала связи и управления скомпрометированной системой со стороны злоумышленников');
INSERT INTO threat_and_sec_schema.main_techniques (id, technique_number, technique_description)
VALUES (118, 2,
