INSERT INTO threat_and_sec_schema.main_techniques (id, technique_number, technique_description)
VALUES (121, 5, 'Отправка данных по известным протоколам управления и передачи данных');
INSERT INTO threat_and_sec_schema.main_techniques (id, technique_number, technique_description)
VALUES (122, 6, 'Отправка данных по собственным протоколам');
INSERT INTO threat_and_sec_schema.main_techniques (id, technique_number, technique_description)
VALUES (123, 7,
        'Проксирование трафика передачи данных для маскировки подозрительной сетевой активности, обхода правил на межсетевом экране и сокрытия адресов инфраструктуры нарушителей, дублирование каналов связи, обфускация и разделение трафика передачи данных во избежание обнаружения. Примеры: 1) использование скомпрометированных систем в той же сети, для которых правилами МЭ разрешен доступ в Интернет в качестве прокси серверов; 2) использование инфраструктуры сети TOR для проксирования запросов к серверам управления; 3) использование одного коммуникационного протокола для запроса, и другого - для ответа на запрос');
INSERT INTO threat_and_sec_schema.main_techniques (id, technique_number, technique_description)
VALUES (124, 8, 'Туннелирование трафика передачи данных через VPN');
INSERT INTO threat_and_sec_schema.main_techniques (id, technique_number, technique_description)
VALUES (125, 9,
        'Туннелирование трафика управления в поля заполнения и данных служебных протоколов, к примеру, туннелирование трафика управления в поля данных и заполнения протоколов DNS, ICMP или другие');
INSERT INTO threat_and_sec_schema.main_techniques (id, technique_number, technique_description)
VALUES (126, 10,
        'Вывод информации через съемные носители, в частности, передача данных между скомпрометированными изолированной системой и подключенной к Интернет системой через носители информации, используемые на обеих системах');
INSERT INTO threat_and_sec_schema.main_techniques (id, technique_number, technique_description)
VALUES (127, 11,
        'Отправка данных через альтернативную среду передачи данных. Пример: вывод конфиденциальной информации через субтитры видеоряда, демонстрируемого на веб-сайте');
INSERT INTO threat_and_sec_schema.main_techniques (id, technique_number, technique_description)
VALUES (129, 13,
